sp_angular_provider.script:
function(){
	return {
		template: "<button ng-if='showhideApproveButton()'" +
		"ng-click='doApproveTimesheet()'" +
		"class='btn btn-default action-btn'>" +
		"${Approve} </button>",
		controller: function($scope){

			$scope.showhideApproveButton = function(){
				if($scope.data.state == "submitted" && 
				  ($scope.data.userID == $scope.data.timesheet_approver ||
				  $scope.data.isAdmin))
					return true;
				return false;
			};

			$scope.doApproveTimesheet = function(){
				$scope.data.action = "approve";
				$scope.data.submit_action = true;
				$scope.server.update();
			};
		}
	};
}
