sp_angular_provider.script:
function() {
	return {
		template: "<button ng-if='showhideSubmitButton()' " +
		"ng-click='doSubmit()' " +
		"class='btn btn-default {{isDisabled}}' >" +
		"${Submit}</button>",
		controller: function($scope, srvMMSManagement){
			console.log("Data in Submit button: ", $scope.data);
			$scope.showhideSubmitButton = function(){
				if($scope.data.state == "draft" && 
				   ($scope.data.hr_profile == $scope.data.userID ||
				   $scope.data.timesheet_approver == $scope.data.userID ||
				   $scope.data.isAdmin))
					return true;
				return false;
			};

			$scope.doSubmit = function(){
				$scope.data.action = "submit";
				$scope.data.submit_action = true;
				$scope.server.update();
			};
		}
	};
}
