sp_angular_provider.script:
function(){
	return {
		template: "<button ng-if='showhideReprocessButton()'" +
		"ng-click='doReprocessTimesheet()'" +
		"class='btn btn-default action-btn'>" +
		"${Reprocess} </button>",
		controller: function($scope, spModal){

			$scope.showhideReprocessButton = function(){
				if($scope.data.state == "submitted" &&
				   ($scope.data.userID == $scope.data.timesheet_approver ||
					$scope.data.isAdmin))
					return true;
				return false;
			};

			$scope.doReprocessTimesheet = function(){
				spModal.prompt("Please give the reason reprocess this timesheet").then(function(reason){
					$scope.data.reason = reason;
					$scope.data.action = "reprocess";
					$scope.data.submit_action = true;
					$scope.server.update();
				});
			};
		}
	};
}
